mapper.update:
UPDATE apply_user
birthday = #{birthday}, sex = #{sex}, nickname = #{nickname}, email = #{email}, qq = #{qq}, avatar = #{avatar}, address = #{address}, description = #{description}, union_id = #{unionId}
WHERE id = #{id}

UPDATE apply_user
SET base_user_id = #{baseUserId},phone = #{phone}
WHERE id = #{id}

UPDATE apply_user
SET union_id = #{unionId}
WHERE id = #{id}

UPDATE apply_user
SET open_id = #{openId},union_id = #{unionId}
WHERE id = #{id}

UPDATE apply_user
SET open_id = '',union_id = ''
WHERE id = #{0}

UPDATE apply_user
SET password = #{password}
WHERE id = #{id}

UPDATE apply_user
SET payment_password = #{paymentPassword}
WHERE id = #{id}

UPDATE apply_user
SET payment_permissions = #{paymentPermissions}
WHERE id = #{id}
