mapper.select:
select a.id,a.phone,open_id,union_id,nickname,avatar,email,qq,address,description,application_type,sex ,birthday,payment_permissions,register_at,balance,member_points, CASE b.locked WHEN 'LOCK' THEN b.locked ELSE a.locked END locked FROM apply_user a LEFT JOIN base_user b ON a.base_user_id = b.id
a.id like concat('%',#{userId},'%') and a.nickname like concat('%',#{nickname},'%') and a.phone like concat('%',#{phone},'%') and a.register_at >= #{createAtStart} and a.register_at < #{createAtEnd} and application_type = #{applicationType}
limit #{startRow},#{rows}

select count(id) from apply_user

    id like concat('%',#{userId},'%')
     and nickname like concat('%',#{nickname},'%')
    and phone like concat('%',#{phone},'%')
     and register_at >= #{createAtStart}
    and register_at < #{createAtEnd}

mapper.update:
UPDATE apply_user
birthday = #{birthday}, sex = #{sex}, nickname = #{nickname}, email = #{email}, qq = #{qq}, avatar = #{avatar}, address = #{address}, description = #{description}, union_id = #{unionId}
WHERE id = #{id}

UPDATE apply_user
SET base_user_id = #{baseUserId},phone = #{phone}
WHERE id = #{id}

UPDATE apply_user
SET union_id = #{unionId}
WHERE id = #{id}

UPDATE apply_user
SET open_id = #{openId},union_id = #{unionId}
WHERE id = #{id}

UPDATE apply_user
SET open_id = '',union_id = ''
WHERE id = #{0}

UPDATE apply_user

    password = #{password},
    payment_password = #{paymentPassword}

WHERE id = #{id}

UPDATE apply_user
SET payment_permissions = #{paymentPermissions}
WHERE id = #{id}
